import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import { motion, AnimatePresence } from "framer-motion";
import ThemeToggle from "./ThemeToggle";

const formatTime = (iso) => {
  const d = new Date(iso);
  return d.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });
};

const ChatWindow = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [bounce, setBounce] = useState(false);
  const messagesRef = useRef(null);

  // auto-scroll
  useEffect(() => {
    messagesRef.current?.scrollTo({
      top: messagesRef.current.scrollHeight,
      behavior: "smooth",
    });
  }, [messages, isTyping]);

  const pushUserMessage = (text) => {
    const msg = { sender: "user", text, ts: new Date().toISOString() };
    setMessages((prev) => [...prev, msg]);
    return msg;
  };

  const pushBotMessage = (text) => {
    const msg = { sender: "bot", text, ts: new Date().toISOString() };
    setMessages((prev) => [...prev, msg]);
  };

  const sendMessage = async (text = input) => {
    if (!text.trim()) return;
    const userMsg = pushUserMessage(text);
    if (text === input) setInput("");
    setIsTyping(true);

    // bounce effect
    setBounce(true);
    setTimeout(() => setBounce(false), 380);

    try {
      const res = await axios.post("http://127.0.0.1:8000/chat", {
        message: text,
      });

      if (res.data?.response) {
        pushBotMessage(res.data.response);
      } else {
        pushBotMessage("No reply");
      }
    } catch (e) {
      setMessages((prev) => [
        ...prev,
        {
          sender: "bot",
          text: "⚠️ Server error",
          ts: new Date().toISOString(),
          error: true,
          retryText: text,
        },
      ]);
    } finally {
      setIsTyping(false);
    }
  };

  return (
    <motion.div
      className="chat-container"
      initial={{ opacity: 0, scale: 0.8 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ duration: 0.45 }}
    >
      {/* Header */}
      <div
        className="chat-header"
        style={{ position: "sticky", top: 0, zIndex: 10 }}
      >
        <div
          className="avatar"
          style={{
            background:
              "linear-gradient(135deg,var(--user-gradient-start),var(--user-gradient-end))",
          }}
        >
          🤖
        </div>
        <div style={{ flex: 1 }}>
          <div className="header-title">DevPal</div>
          <div
            className="header-sub"
            style={{ display: "flex", alignItems: "center", gap: 8 }}
          >
            <div className="status-dot" />{" "}
            <span style={{ color: "rgba(255,255,255,0.95)" }}>Online</span>
          </div>
        </div>

        <div style={{ display: "flex", gap: 8 }}>
          <ThemeToggle />
        </div>
      </div>

      {/* Messages */}
      <div className="messages" ref={messagesRef}>
        <AnimatePresence initial={false}>
          {messages.map((m, i) => (
            <motion.div
              key={i}
              initial={{ opacity: 0, y: m.sender === "user" ? 10 : -10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0 }}
              transition={{ duration: 0.28 }}
            >
              <div
                className={`message-bubble ${
                  m.error
                    ? "msg-error"
                    : m.sender === "user"
                    ? "msg-user"
                    : "msg-bot"
                }`}
              >
                <div>{m.text}</div>
                <span className="msg-meta">{formatTime(m.ts)}</span>
                {m.error && (
                  <motion.button
                    className="retry-btn"
                    onClick={() => sendMessage(m.retryText)}
                    animate={{ scale: [1, 1.15, 1] }}
                    transition={{
                      repeat: Infinity,
                      duration: 1.2,
                      ease: "easeInOut",
                    }}
                  >
                    Retry
                  </motion.button>
                )}
              </div>
            </motion.div>
          ))}
        </AnimatePresence>

        {isTyping && <div className="typing">DevPal is typing…</div>}
      </div>

      {/* Input */}
      <div className="input-area">
        <input
          placeholder="Type a message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === "Enter" && !e.shiftKey) {
              e.preventDefault();
              sendMessage();
            }
          }}
        />
        <motion.button
          className="send-button"
          onClick={() => sendMessage()}
          whileTap={{ scale: 0.95 }}
          animate={
            bounce ? { scale: [1, 1.25, 0.95, 1] } : {}
          }
        >
          Send
        </motion.button>
      </div>
    </motion.div>
  );
};

export default ChatWindow;
