import React, { useState } from "react";
import axios from "axios";
import Lottie from "lottie-react";
import robotAnimation from "../assets/robot.json"; // download a Lottie JSON

const ChatWindow = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { sender: "user", text: input }];
    setMessages(newMessages);
    setInput("");
    setIsTyping(true);

    try {
      const response = await axios.post("http://127.0.0.1:8000/chat", {
        message: input,
      });

      const botReply = response.data.reply || "ü§ñ ...";
      setMessages([...newMessages, { sender: "bot", text: botReply }]);
    } catch (error) {
      console.error("Error talking to backend:", error);
      setMessages([...newMessages, { sender: "bot", text: "‚ö†Ô∏è Server error" }]);
    } finally {
      setIsTyping(false);
    }
  };

  return (
    <div
      style={{
        width: "420px",
        height: "550px",
        border: "1px solid #ddd",
        borderRadius: "12px",
        display: "flex",
        flexDirection: "column",
        backgroundColor: "#f9f9f9",
        boxShadow: "0px 4px 12px rgba(0,0,0,0.1)",
      }}
    >
      {/* Header */}
      <div
        style={{
          padding: "12px",
          borderBottom: "1px solid #eee",
          background: "#007bff",
          color: "white",
          fontWeight: "bold",
          textAlign: "center",
          borderTopLeftRadius: "12px",
          borderTopRightRadius: "12px",
        }}
      >
        ü§ñ DevPal Chat
      </div>

      {/* Messages */}
      <div
        style={{
          flex: 1,
          overflowY: "auto",
          padding: "12px",
          display: "flex",
          flexDirection: "column",
          gap: "8px",
        }}
      >
        {messages.map((msg, index) => (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent:
                msg.sender === "user" ? "flex-end" : "flex-start",
              alignItems: "center",
              gap: "8px",
              animation: "fadeIn 0.3s ease",
            }}
          >
            {msg.sender === "bot" && (
              <Lottie
                animationData={robotAnimation}
                loop
                style={{ width: 40, height: 40 }}
              />
            )}
            <div
              style={{
                maxWidth: "70%",
                padding: "10px",
                borderRadius: "12px",
                background: msg.sender === "user" ? "#007bff" : "#eaeaea",
                color: msg.sender === "user" ? "white" : "black",
                fontSize: "14px",
                boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
              }}
            >
              <b>{msg.sender === "user" ? "You" : "DevPal"}</b>
              <div>{msg.text}</div>
            </div>
          </div>
        ))}

        {/* Typing Indicator */}
        {isTyping && (
          <div
            style={{
              display: "flex",
              alignItems: "center",
              gap: "6px",
              marginTop: "6px",
            }}
          >
            <Lottie
              animationData={robotAnimation}
              loop
              style={{ width: 40, height: 40 }}
            />
            <div
              style={{
                padding: "8px 12px",
                background: "#eaeaea",
                borderRadius: "12px",
                fontSize: "14px",
                color: "#555",
              }}
            >
              <span className="dot-flashing"></span> Typing...
            </div>
          </div>
        )}
      </div>

      {/* Input */}
      <div
        style={{
          display: "flex",
          padding: "10px",
          borderTop: "1px solid #eee",
          background: "white",
        }}
      >
        <textarea
          rows={1}
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
          onKeyDown={(e) => {
            if (e.key === "Enter" && !e.shiftKey) {
              e.preventDefault();
              sendMessage();
            }
          }}
          style={{
            flex: 1,
            padding: "10px",
            borderRadius: "8px",
            border: "1px solid #ccc",
            outline: "none",
            fontSize: "14px",
            resize: "none",
          }}
        />
        <button
          onClick={sendMessage}
          style={{
            marginLeft: "8px",
            padding: "10px 16px",
            background: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "8px",
            cursor: "pointer",
          }}
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatWindow;
