import React, { useState } from "react";
import axios from "axios";
import { motion, AnimatePresence } from "framer-motion";

const ChatWindow = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { sender: "user", text: input }];
    setMessages(newMessages);
    setInput("");
    setIsTyping(true);

    try {
      const response = await axios.post("http://127.0.0.1:8000/chat", {
        user_input: input,
      });
      const botReply = response.data.reply || "No reply";
      setMessages([...newMessages, { sender: "bot", text: botReply }]);
    } catch (error) {
      setMessages([...newMessages, { sender: "bot", text: "⚠️ Server error" }]);
    }

    setIsTyping(false);
  };

  return (
    <motion.div
      className="chat-container"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      style={{
        width: "420px",
        height: "520px",
        border: "1px solid #ccc",
        borderRadius: "12px",
        display: "flex",
        flexDirection: "column",
        backgroundColor: "#fefefe",
        padding: "12px",
        boxShadow: "0 8px 24px rgba(0,0,0,0.15)",
      }}
    >
      {/* Chat Header */}
      <motion.div
        className="chat-header"
        initial={{ y: -30, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.6 }}
        style={{
          padding: "10px",
          fontWeight: "bold",
          fontSize: "18px",
          textAlign: "center",
          background: "#007bff",
          color: "white",
          borderRadius: "8px",
          marginBottom: "10px",
        }}
      >
        🤖 DevPal AI Bot
      </motion.div>

      {/* Messages */}
      <div
        style={{ flex: 1, overflowY: "auto", marginBottom: "10px" }}
      >
        <AnimatePresence>
          {messages.map((msg, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, x: msg.sender === "user" ? 50 : -50 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0 }}
              transition={{ duration: 0.3 }}
              style={{
                textAlign: msg.sender === "user" ? "right" : "left",
                margin: "5px 0",
              }}
            >
              <span
                style={{
                  display: "inline-block",
                  padding: "10px",
                  borderRadius: "10px",
                  background: msg.sender === "user" ? "#007bff" : "#e5e5ea",
                  color: msg.sender === "user" ? "white" : "black",
                  maxWidth: "75%",
                }}
              >
                {msg.text}
              </span>
            </motion.div>
          ))}
        </AnimatePresence>

        {isTyping && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ repeat: Infinity, duration: 1 }}
            style={{ fontStyle: "italic", color: "#666", paddingLeft: "8px" }}
          >
            Bot is typing...
          </motion.div>
        )}
      </div>

      {/* Input Area */}
      <div style={{ display: "flex" }}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
          style={{
            flex: 1,
            padding: "10px",
            border: "1px solid #ddd",
            borderRadius: "6px",
          }}
          placeholder="Type a message..."
        />
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.9 }}
          onClick={sendMessage}
          style={{
            marginLeft: "6px",
            padding: "10px 14px",
            background: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "6px",
          }}
        >
          Send
        </motion.button>
      </div>
    </motion.div>
  );
};

export default ChatWindow;
